$(document).ready(function () {
  function applyStyles(lang, theme) {
    let direction = lang === "ar" ? "rtl" : "ltr";
    let textAlign = lang === "ar" ? "right" : "left";
    $("body").css({ "direction": direction, "text-align": textAlign });
    $(".owl-carousel").owlCarousel({ rtl: true, });
    $(".lang").css("direction", direction);
    $(".translate").attr("id", lang === "ar" ? "en" : "ar").html(lang === "ar" ?
      `<img src="dist/img/en.svg" alt="" width="20px"> English</button>` :
      `<img src="dist/img/soudia.png" alt="" width="20px"> العربية</button>`);
    $(".translate").attr("id") == 'ar' ?
      $("body").css("font-family", "Montserrat") :
      $("body").css("font-family", "IBMPlexSansArabic");
    $(".translate_btn").attr("id") == 'ar' ?
      $(".translate_btn").css("font-family", "IBMPlexSansArabic") :
      $(".translate_btn").css("font-family", "Montserrat");
    // console.log(lang)

    lang === "ar" ? $("#i__phone").attr("src", 'dist/img/Floating-iPhone-Ar-2-min.png') : $("#i__phone").attr("src", 'dist/img/Floating-iPhone-En-2-min.png');
    lang === "ar" ? $(".splash_bg2").css("background-image", 'url(dist/img/left__bg.png)') :
      $(".splash_bg2").attr("background-image", 'url(./dist/img/right__bg.png)');

    lang === "en" ? $(".splash_bg2").css("background-image", 'url(dist/img/right__bg.png)') :
      $(".splash_bg2").attr("background-image", 'url(dist/img/left__bg.png)');


  }

  function updateLanguage(lang) {
    localStorage.setItem("langu", lang);
    let locallang = localStorage.getItem("langu");
    $(".lang").each(function () {
      if (arrlang && arrlang[locallang]) {
        $(this).html(arrlang[locallang][$(this).attr("key")]);
      }
    });
    applyStyles(locallang);
  }

  $(".translate").click(function () {
    let lang = $(this).attr("id");
    updateLanguage(lang);
  });

  let storedLang = localStorage.getItem("langu");
  if (storedLang) {
    updateLanguage(storedLang);
  }

});


// start counter number
let nums = document.querySelectorAll(".nums .num");
let section = document.querySelector(".three");
let started = false; // Function Started ? No

window.onscroll = function () {
  if (window.scrollY >= section.offsetTop - window.innerHeight) {
    if (!started) {
      nums.forEach((num) => startCount(num));
    }
    started = true;
  }
};

function startCount(el) {
  let goal = el.dataset.goal;
  let count = setInterval(() => {
    el.textContent++;
    if (el.textContent == goal) {
      clearInterval(count);
    }
  }, 2000 / goal);
}

// end counter number

